const { createClient } = require('redis');
const queueKey = $('When Executed by Another Workflow').first().json.queueKey;
const lockKey = $('When Executed by Another Workflow').first().json.lockKey;
const client = createClient({
  socket: { host: 'redis', port: 6379 },
  database: 10
});

let queueValue = null;
let queueLength = 0;
let lockValue = null;
let deletedCount = 0;

try {
  await client.connect();

  // Captura valor do lock (string)
  lockValue = await client.get(lockKey);

  // Captura valor da queue (lista) - pega toda a lista
  queueValue = await client.lRange(queueKey, 0, -1);

  // Conta quantos itens tinha na fila
  queueLength = await client.lLen(queueKey);

  // Apaga as chaves
  deletedCount = await client.del([queueKey, lockKey]);

} catch (error) {
  throw new Error(`Falha na opera√ß√£o Redis: ${error.message}`);
} finally {
  try {
    if (client && client.isOpen) {
      await client.quit();
    }
  } catch (closeError) {
    console.error('Erro ao fechar conex√£o Redis:', closeError.message);
  }
}

return {
  queueKey: queueKey,
  queueValue: queueValue,
  queueLength: queueLength,  // ‚Üê Quantidade de itens na fila
  lockKey: lockKey, 
  lockValue: lockValue,
  deletedCount: deletedCount
};

