input = $('Mrg - Input Data').first().json;
// --- L√≥gica de Vari√°veis ---
const replyToId = input.toSend.body.content_attributes?.in_reply_to;
// Acessa o array de anexos de forma segura
const sourceAttachments = input.toSend.body.attachments;
// Calcula a quantidade de anexos. Usa o `?? 0` para garantir que seja um n√∫mero (0 se n√£o houver anexos).
const attachmentsCount = sourceAttachments?.length ?? 0;
// --- Objeto de Retorno ---
return {
  event: input.check.body.event,
  isPrivate: input.check.body.private,
  agentName: input.toSend.body.conversation.messages[0].sender.available_name,
  message: {
    chatId: input.toSend.body.conversation.meta.sender.identifier,
    content: input.toSend.body.content,
    inReplyTo: {
      isReply: !!replyToId,
      ...(replyToId && { messageId: replyToId }),
    },
    // --- L√≥gica de Anexos Atualizada ---
    attachments: {
      attachmentsCount: attachmentsCount,
      // Adiciona a propriedade 'content' apenas se a contagem for maior que 0
      ...(attachmentsCount > 0 && {
        content: sourceAttachments.map(att => {
          const baseAttachment = {
            id: att.id,
            type: att.file_type,
            url: att.data_url
          };

          // Adiciona fileName se o file_type for "file"
          if (att.file_type === 'file') {
            // Extrai o nome do arquivo da URL (√∫ltima parte ap√≥s a /)
            const urlParts = att.data_url.split('/');
            const encodedFileName = urlParts[urlParts.length - 1];
            // Decodifica o nome do arquivo
            baseAttachment.fileName = decodeURIComponent(encodedFileName);
          }

          return baseAttachment;
        })
      })
    },
  },
};

const { createClient } = require('redis');

var data = $input.item.json;
var queueKey = data.queueKey;
const executionId = $execution.id;
const processingQueueKey = `processing:exec:${executionId}`;

const client = createClient({
  socket: {
    host: 'redis',
    port: 6379
  },
  database: 10
});

let movedPayloadString = null;

try {
  await client.connect();
  //const poppedPayloadString = await client.lPop(queueKey); // Puxa o primeiro item (payload) da fila. ‚Üê DEPRECATED!

  movedPayloadString = await client.lMove(queueKey, processingQueueKey, 'LEFT', 'RIGHT'); // Move a mensagem mais antiga da fila principal para a fila de trabalho √∫nica ‚Üê LATEST!

  if (movedPayloadString) { // Se um payload foi retornado (movido), processa-o
    await client.expire(processingQueueKey, 3600); //1h Apenas para limpeza. Worktable ef√™mero.
    const remainingCount = await client.lLen(queueKey); // Obt√©m a quantidade de itens restantes na fila (AP√ìS remo√ß√£o do atual!) para debug.

    movedPayloadString = JSON.parse(movedPayloadString); //Parseia o payload retornado.
    $input.item.json = { ...data, ...movedPayloadString }; // Somar este payload √† queueKey & lockKey do input.
    $input.item.json.lpopResult = `Fila [${queueKey}] agora tem ${remainingCount} itens`; // Debug para execution data (s√≥ faz sentido em auto trigger, n√£o em loop... ou, faz em caso de falha)
  } else {
    // Se n√£o h√° mais mensagens para processar, zeramos a fila, precisamos remover o lock. Vou fazer logo aqui em c√≥digo pra n√£o usar outro node Redis.
    await client.del(data.lockKey);
    $input.item.json.lpopResult = `Fila [${queueKey}] esvaziada. Lock removido!`;
  }

} catch (error) {
  throw error;
} finally {
  if (client.isOpen) {
    await client.quit();
  }
}

return $input.item;
